          //alert(newArticleId + "번 게시물 등록 완료!!");

          router.replace("detail?id=" + newArticleId);
        });
      }

    return{
      newArticleBoardIdElRef,
      newArticleTitleElRef,
      newArticleBodyElRef,
      checkAndWriteArticle
      
    }

  }
  
})
</script>

