	private void addReviewList(Expert expert) {
		String relTypeCode = "expert";
		List<Review> reviews = reviewService.getForPrintReviews(relTypeCode);
		
		for(Review review : reviews) {
			if(review != null && review.getRelId() == expert.getId()) {
