    function returnToString(stepLevel:any) {
      let stepLevelToStr = ''; 
      if(stepLevel == 1){
        stepLevelToStr = 'ìš”ì²­ì„œ ê²€í† ì¤‘';
      }
      if(stepLevel == 2){
        stepLevelToStr = 'ì™¸ë¢°ìŠ¹ì¸(ì¥ë¡€ì¤€ë¹„ì¤‘)';
      }
      if(stepLevel == 3){
        stepLevelToStr = 'ì¥ë¡€ì§„í–‰ì¤‘';
