			{		
				//	loginId:loginId도 맞다
				// 최신 JS에서는 이 방식이 가능함
				loginId:loginId
			},
			function(data){
				if( data.code.substring(0,2) == "S-"){
					alert(data.msg + " (" + data.code + ")");
					checkedDupId = data.loginId;
				
				}
				else{
