		if (form.loginPw.value != form.loginPwConfirm.value) {
			alert("PW가 일치하지 않습니다. PW를 확인해 주세요.");
			form.loginPwConfirm.focus();
			
			return;
		}
		
		if (form.name.value.trim().length == 0) {
			alert("이름을 입력해주세요.");
			form.name.focus();
			
			return;
		}
		if (form.nickname.value.trim().length == 0) {
